{
    "type": "AdaptiveCard",
    "body": [
        {
            "type": "ColumnSet",
            "columns": [
                {
                    "type": "Column",
                    "width": "auto",
                    "items": [
                        {
                            "type": "ImageSet",
                            "images": [
                                {
                                    "type": "Image",
                                    "size": "Medium",
                                    "url": "data:image/png;base64,",
                                    "spacing": "None",
                                    "horizontalAlignment": "Center"
                                }
                            ]
                        }
                    ]
                },
                {
                    "type": "Column",
                    "items": [
                        {
                            "type": "TextBlock",
                            "weight": "Bolder",
                            "text": "Approvals",
                            "wrap": true
                        },
                        {
                            "type": "TextBlock",
                            "spacing": "None",
                            "text": "Created @{outputs('Start_an_approval')?['body/responseDate']}",
                            "isSubtle": true,
                            "wrap": true
                        }
                    ],
                    "width": "stretch"
                }
            ]
        },
        {
            "type": "TextBlock",
            "text": "Your approval request has been approved by @{outputs('Start_an_approval')?['body/responder/displayName']}",
            "wrap": true
        },
        {
            "type": "FactSet",
            "facts": [
                {
                    "title": "Comments",
                    "value": "@{outputs('Start_an_approval')?['body/comments']}"
                }
            ]
        }
    ],
    "actions": [
        {
            "type": "Action.OpenUrl",
            "title": "View",
            "url": "https://teams.microsoft.com/l/task/7c316234-ded0-4f95-8a83-8453d0876592?url=https%3A%2F%2Fapprovals.teams.microsoft.com%2Fweb%2Fdetails%2FBasic%2F@{outputs('Start_an_approval')?['body/name']}%2F~default&width=600&height=600",
            "iconUrl": "data:image/png;base64,"
        },
        {
            "type": "Action.OpenUrl",
            "title": "Contact approver",
            "iconUrl": "data:image/png;base64,",
            "url": "https://teams.microsoft.com/l/chat/0/0?users=@{outputs('Start_an_approval')?['body/responder/email']},&topicName=Approval%20Request"
        }
    ],
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.4",
    "msteams": {
        "width": "Full"
    }
}

